const burger=()=>{const e=document.querySelector(".menu-burger"),t=document.querySelector(".header-menu"),c=document.querySelectorAll(".header-menu__item");e.addEventListener("click",()=>{e.classList.toggle("active"),e.classList.contains("active")?(document.body.style.overflow="hidden",t.classList.add("active"),c.forEach(e=>{e.classList.add("active")})):(document.body.removeAttribute("style"),t.classList.remove("active"),c.forEach(e=>{e.classList.remove("active")}))})};burger();